mapper.select:
select count(*) from SYS_LOG


        AND  CREATED_BY like CONCAT('%',CONCAT(#{username},'%'))


        AND IP = #{ip}


        AND DATE_FORMAT(CREATION_DATE,'%Y-%m-%d') >= #{startDate}


        AND DATE_FORMAT(CREATION_DATE,'%Y-%m-%d')  <= #{endDate}

select * from SYS_LOG


        AND CREATED_BY like CONCAT('%',CONCAT(#{username},'%'))


        AND IP = #{ip}


        AND DATE_FORMAT(CREATION_DATE,'%Y-%m-%d') >= #{startDate}


        AND DATE_FORMAT(CREATION_DATE,'%Y-%m-%d')  <= #{endDate}

 ORDER BY CREATION_DATE DESC LIMIT #{startIndex},#{pageSize}
